script:
frappe.ui.form.on('Batch AMB', {
    // Initialize form
    onload: function(frm) {
        console.log('Batch AMB form loaded');
        
        // Ensure we're in form view, not tree view
        if (frm.meta.default_view === 'Tree') {
            frm.set_df_property('default_view', 'List');
            console.log('Switched from Tree to List view');
        }
        
        // Set up field requirements
        frm.set_df_property('work_order_ref', 'reqd', 1);
        frm.set_df_property('production_plant_name', 'reqd', 1);
        frm.set_df_property('custom_batch_level', 'reqd', 1);
        
        // Add custom buttons for batch generation
        frm.add_custom_button(__('Generate Level 1 Batch'), function() {
            generate_batch_code(frm, '1');
        }, __('Batch Actions'));
        
        frm.add_custom_button(__('Generate Child Batch'), function() {
            if (!frm.doc.custom_batch_level) {
                frappe.msgprint(__('Please select batch level first'));
                return;
            }
            generate_batch_code(frm, frm.doc.custom_batch_level);
        }, __('Batch Actions'));
        
        // Add button to switch to form view if needed
        if (window.location.href.includes('tree')) {
            frm.add_custom_button(__('Switch to Form View'), function() {
                window.location.href = '/app/batch-amb';
            }, __('View'));
        }
    },

    // After form is rendered
    refresh: function(frm) {
        console.log('Form refreshed');
        
        // Ensure save buttons are visible
        if (frm.page && frm.page.btn_primary) {
            frm.page.btn_primary.show();
            frm.page.btn_secondary.show();
        }
        
        // Check if we're in a dialog/preview and force full form if needed
        if (frm.is_dirty() && !frm.doc.__islocal && frm.meta.issingle == 0) {
            setTimeout(function() {
                if (frm.page && frm.page.btn_primary && frm.page.btn_primary.is(':hidden')) {
                    console.log('Enabling save buttons for existing document');
                    frm.page.btn_primary.show();
                    frm.page.btn_secondary.show();
                    frm.enable_save();
                }
            }, 1000);
        }
    },

    // When work order is selected
    work_order_ref: function(frm) {
        if (frm.doc.work_order_ref) {
            console.log('Work Order selected:', frm.doc.work_order_ref);
            
            // Fetch work order details using your server API
            frappe.call({
                method: 'frappe.client.get_value',
                args: {
                    doctype: 'Work Order',
                    fieldname: ['production_item', 'name', 'creation', 'sales_order'],
                    filters: { name: frm.doc.work_order_ref }
                },
                callback: function(r) {
                    if (r.message) {
                        console.log('Work Order details fetched:', r.message);
                        
                        // Set item to manufacture
                        frm.set_value('item_to_manufacture', r.message.production_item);
                        
                        // Extract consecutive number from work order name (last 3 digits)
                        let wo_name = r.message.name;
                        let consecutive_match = wo_name.match(/\d{3}$/);
                        if (consecutive_match) {
                            frm.set_value('consecutive_number', parseInt(consecutive_match[0]));
                        } else {
                            // Fallback: use creation timestamp
                            frm.set_value('consecutive_number', 1);
                        }
                        
                        // Extract year from creation date
                        let creation_date = new Date(r.message.creation);
                        let year = creation_date.getFullYear().toString().slice(-2);
                        frm.set_value('lft', year);
                        
                        // Set sales order if available
                        if (r.message.sales_order) {
                            frm.set_value('sales_order_related', r.message.sales_order);
                        }
                    }
                }
            });
        }
    },

    // When production plant is selected
    production_plant_name: function(frm) {
        if (frm.doc.production_plant_name) {
            console.log('Production Plant selected:', frm.doc.production_plant_name);
            
            // Extract plant code (first character or numeric value from name)
            let plant_code = extract_plant_code(frm.doc.production_plant_name);
            frm.set_value('rgt', plant_code);
            console.log('Plant code extracted:', plant_code);
        }
    },

    // When batch level changes
    custom_batch_level: function(frm) {
        if (frm.doc.custom_batch_level) {
            console.log('Batch Level changed to:', frm.doc.custom_batch_level);
            
            if (frm.doc.custom_batch_level > '1') {
                frm.set_df_property('parent_batch_amb', 'reqd', 1);
                
                // Auto-fetch possible parents based on level
                if (frm.doc.work_order_ref) {
                    fetch_possible_parents(frm);
                }
            } else {
                frm.set_df_property('parent_batch_amb', 'reqd', 0);
                frm.set_value('parent_batch_amb', '');
            }
            
            // Auto-generate batch code if all required fields are present
            if (frm.doc.work_order_ref && frm.doc.production_plant_name && frm.doc.item_to_manufacture) {
                setTimeout(function() {
                    generate_batch_code(frm, frm.doc.custom_batch_level);
                }, 500);
            }
        }
    },

    // When parent batch is selected
    parent_batch_amb: function(frm) {
        if (frm.doc.parent_batch_amb && frm.doc.custom_batch_level > '1') {
            console.log('Parent Batch selected:', frm.doc.parent_batch_amb);
            
            // Verify parent level is appropriate using your server API
            frappe.call({
                method: 'frappe.client.get_value',
                args: {
                    doctype: 'Batch AMB',
                    fieldname: ['custom_batch_level', 'custom_generated_batch_name'],
                    filters: { name: frm.doc.parent_batch_amb }
                },
                callback: function(r) {
                    if (r.message) {
                        let parent_level = parseInt(r.message.custom_batch_level);
                        let current_level = parseInt(frm.doc.custom_batch_level);
                        
                        if (parent_level !== current_level - 1) {
                            frappe.msgprint(__('Parent batch must be level ' + (current_level - 1)));
                            frm.set_value('parent_batch_amb', '');
                        } else {
                            console.log('Parent validation passed');
                        }
                    }
                }
            });
        }
    },

    // Before saving the document
    before_save: function(frm) {
        console.log('Before save validation');
        
        // Validate batch level and parent relationship
        if (frm.doc.custom_batch_level > '1' && !frm.doc.parent_batch_amb) {
            frappe.throw(__('Parent Batch AMB is required for level ' + frm.doc.custom_batch_level));
        }
        
        // Validate batch code uniqueness using your server API
        if (frm.doc.custom_generated_batch_name) {
            validate_batch_uniqueness(frm);
        }
        
        // Set title same as batch code
        if (frm.doc.custom_generated_batch_name && !frm.doc.title) {
            frm.set_value('title', frm.doc.custom_generated_batch_name);
        }
        
        // Ensure lft and rgt are properly set for tree structure
        if (!frm.doc.lft || !frm.doc.rgt) {
            frm.set_value('lft', '0001');
            frm.set_value('rgt', '0002');
        }
    }
});

// Extract plant code from production plant name
function extract_plant_code(plant_name) {
    // Try to extract numeric code from name
    let numeric_match = plant_name.match(/\d+/);
    if (numeric_match) {
        return numeric_match[0].charAt(0); // First digit of the number
    }
    
    // Fallback: first character of the plant name
    return plant_name.charAt(0);
}

// Fetch possible parent batches for the current level
function fetch_possible_parents(frm) {
    let parent_level = parseInt(frm.doc.custom_batch_level) - 1;
    
    frappe.call({
        method: 'frappe.client.get_list',
        args: {
            doctype: 'Batch AMB',
            filters: {
                'work_order_ref': frm.doc.work_order_ref,
                'custom_batch_level': parent_level.toString()
            },
            fields: ['name', 'custom_generated_batch_name', 'title'],
            limit: 50
        },
        callback: function(r) {
            if (r.message && r.message.length > 0) {
                console.log('Available parents:', r.message);
                // You could populate a dropdown or suggest parents here
            }
        }
    });
}

// Function to generate batch code (integrated with your server API)
function generate_batch_code(frm, level) {
    console.log('Generating batch code for level:', level);
    
    // Validate required fields
    if (!frm.doc.work_order_ref) {
        frappe.msgprint(__('Work Order Reference is required'));
        return;
    }
    
    if (!frm.doc.production_plant_name) {
        frappe.msgprint(__('Production Plant is required'));
        return;
    }
    
    if (!frm.doc.item_to_manufacture) {
        frappe.msgprint(__('Item to Manufacture is required'));
        return;
    }
    
    // Use your server API for batch generation to ensure consistency
    frappe.call({
        method: 'batch_amb_custom_tree_api.add_batch_node',
        args: {
            doctype: 'Batch AMB',
            is_group: (level === '1' || level === '2') ? 1 : 0,
            work_order_ref: frm.doc.work_order_ref,
            parent: frm.doc.parent_batch_amb || null,
            is_root: (level === '1')
        },
        callback: function(r) {
            if (r.message) {
                console.log('Batch node created via API:', r.message);
                
                // Now generate the actual batch code
                generate_actual_batch_code(frm, level, r.message.name);
            }
        },
        error: function(err) {
            console.error('API Error:', err);
            frappe.msgprint(__('Error creating batch node: ') + err.message);
        }
    });
}

// Generate the actual batch code format
function generate_actual_batch_code(frm, level, batch_name) {
    // Get base components
    let product_code = frm.doc.item_to_manufacture.substring(0, 4);
    let consecutive = frm.doc.consecutive_number ? frm.doc.consecutive_number.toString().padStart(3, '0') : '001';
    let year = frm.doc.lft || new Date().getFullYear().toString().slice(-2);
    let plant_code = frm.doc.rgt || extract_plant_code(frm.doc.production_plant_name);
    
    console.log('Base components:', { product_code, consecutive, year, plant_code });
    
    // Generate base batch code (level 1)
    let base_batch_code = `${product_code}-${consecutive}-${year}-${plant_code}`;
    let final_batch_code = base_batch_code;
    
    // Add level-specific suffix
    switch(level) {
        case '2':
            get_next_consecutive(frm, level, function(next_num) {
                final_batch_code = base_batch_code + `-${next_num}`;
                set_final_batch_values(frm, final_batch_code, level, next_num, batch_name);
            });
            break;
            
        case '3':
            get_next_consecutive(frm, level, function(next_num) {
                final_batch_code = base_batch_code + `-${next_num.toString().padStart(3, '0')}`;
                set_final_batch_values(frm, final_batch_code, level, next_num, batch_name);
            });
            break;
            
        case '4':
            get_next_consecutive(frm, level, function(next_num) {
                final_batch_code = base_batch_code + `-P${next_num.toString().padStart(3, '0')}`;
                set_final_batch_values(frm, final_batch_code, level, next_num, batch_name);
            });
            break;
            
        default: // Level 1
            set_final_batch_values(frm, base_batch_code, '1', parseInt(consecutive), batch_name);
    }
}

// Get next consecutive number for child batches
function get_next_consecutive(frm, level, callback) {
    let filters = {
        'work_order_ref': frm.doc.work_order_ref,
        'custom_batch_level': level
    };
    
    if (frm.doc.parent_batch_amb) {
        filters['parent_batch_amb'] = frm.doc.parent_batch_amb;
    }
    
    frappe.call({
        method: 'frappe.client.get_list',
        args: {
            doctype: 'Batch AMB',
            filters: filters,
            fields: ['consecutive_number'],
            order_by: 'consecutive_number desc',
            limit: 1
        },
        callback: function(r) {
            let next_num = 1;
            if (r.message && r.message.length > 0) {
                next_num = (r.message[0].consecutive_number || 0) + 1;
            }
            console.log('Next consecutive for level', level, ':', next_num);
            callback(next_num);
        }
    });
}

// Set final batch values after generation
function set_final_batch_values(frm, batch_code, level, consecutive_num, batch_name) {
    frm.set_value('custom_generated_batch_name', batch_code);
    frm.set_value('title', batch_code);
    frm.set_value('custom_batch_level', level);
    frm.set_value('consecutive_number', consecutive_num);
    frm.set_value('name', batch_name); // Set the name from API
    
    // Set is_group based on level
    if (level === '1' || level === '2') {
        frm.set_value('is_group', 1);
    } else {
        frm.set_value('is_group', 0);
    }
    
    console.log('Final batch code generated:', batch_code);
    frappe.show_alert(__('Batch code generated: ') + batch_code);
    
    // Refresh the form to show all values
    frm.refresh();
}

// Validate batch code uniqueness
function validate_batch_uniqueness(frm) {
    frappe.call({
        method: 'frappe.client.get_list',
        args: {
            doctype: 'Batch AMB',
            filters: {
                'custom_generated_batch_name': frm.doc.custom_generated_batch_name,
                'name': ['!=', frm.doc.name || '']
            },
            limit: 1
        },
        callback: function(r) {
            if (r.message && r.message.length > 0) {
                frappe.msgprint(__('Batch code already exists: ') + frm.doc.custom_generated_batch_name);
                frm.set_value('custom_generated_batch_name', '');
            }
        }
    });
}

// Force form view if we're stuck in tree view
if (window.location.href.includes('/tree/')) {
    console.log('Redirecting from tree view to form view');
    window.location.href = window.location.href.replace('/tree/', '/list/');
}

// Ensure save buttons are always visible
$(document).on('click', function() {
    let primary_btn = $('.btn-primary').filter(':visible');
    let secondary_btn = $('.btn-secondary').filter(':visible');
    
    if (primary_btn.length === 0 || secondary_btn.length === 0) {
        $('.btn-primary').show();
        $('.btn-secondary').show();
    }
});

// Enhanced Batch AMB Client Script - Container & Barrel Weight Management
// Supports Level 3 containers, Level 4 scanning, and P-INV/P-VTA Work Orders

frappe.ui.form.on('Batch AMB', {
    onload: function(frm) {
        if (frm.is_new()) {
            frm.set_value('custom_batch_level', '1');
            frm.set_value('is_group', 1);
        }
        if (frm.doc.custom_batch_level == '3') {
            initialize_barrel_management(frm);
        }
    },

    refresh: function(frm) {
        if (should_auto_generate(frm)) {
            generate_batch_code(frm);
        }
        add_level_specific_buttons(frm);
        if (frm.doc.custom_batch_level == '3') {
            update_weight_totals(frm);
        }
    },
    
    after_save: function(frm) {
        // Refresh the form to get the latest values from the server
        frm.refresh();
    },

    work_order_ref: function(frm) {
        if (frm.doc.work_order_ref) {
            fetch_work_order_data(frm);
        }
    },

    custom_batch_level: function(frm) {
        if (!frm.is_new()) {
            frappe.msgprint('Cannot change batch level of existing documents. Create a new document for different levels.');
            frm.set_value('custom_batch_level', frm.doc.__original_level || '1');
            return;
        }
        configure_level_settings(frm);
        if (should_auto_generate(frm)) {
            generate_batch_code(frm);
        }
    },

    parent_batch_amb: function(frm) {
        if (frm.doc.parent_batch_amb === frm.doc.name) {
            frappe.msgprint('A batch cannot be its own parent');
            frm.set_value('parent_batch_amb', '');
            return;
        }
        if (frm.doc.parent_batch_amb && should_auto_generate(frm)) {
            generate_batch_code(frm);
        }
    },

    quick_barcode_scan: function(frm) {
        if (frm.doc.quick_barcode_scan && frm.doc.custom_batch_level == '3') {
            process_quick_barcode_scan(frm);
        }
    },

    default_packaging_type: function(frm) {
        if (frm.doc.default_packaging_type) {
            fetch_default_tara_weight(frm);
        }
    },

    before_save: function(frm) {
        if (frm.doc.parent_batch_amb === frm.doc.name) {
            frappe.throw('A batch cannot be its own parent');
            return false;
        }
        if (parseInt(frm.doc.custom_batch_level || '0', 10) > 1 && !frm.doc.parent_batch_amb) {
            frappe.throw('Parent Batch AMB is required for level ' + frm.doc.custom_batch_level);
            return false;
        }
        if (!frm.doc.__original_level) {
            frm.doc.__original_level = frm.doc.custom_batch_level;
        }
        if (frm.doc.custom_batch_level == '3') {
            validate_barrel_data(frm);
        }
        return true;
    }
});

// Child Table events
frappe.ui.form.on('Container Barrels', {
    container_barrels_add: function(frm, cdt, cdn) {
        const row = locals[cdt][cdn];
        if (frm.doc.default_packaging_type) {
            frappe.model.set_value(cdt, cdn, 'packaging_type', frm.doc.default_packaging_type);
        }
        generate_barrel_serial_number(frm, row);
    },

    barcode_scan_input: function(frm, cdt, cdn) {
        const row = locals[cdt][cdn];
        if (row.barcode_scan_input) {
            process_barcode_scan(frm, cdt, cdn, row.barcode_scan_input);
        }
    },

    packaging_type: function(frm, cdt, cdn) {
        const row = locals[cdt][cdn];
        if (row.packaging_type) {
            fetch_tara_weight_for_row(frm, cdt, cdn);
        }
    },

    gross_weight: function(frm, cdt, cdn) {
        calculate_net_weight(frm, cdt, cdn);
        update_weight_totals(frm);
    },

    tara_weight: function(frm, cdt, cdn) {
        calculate_net_weight(frm, cdt, cdn);
        update_weight_totals(frm);
    },

    container_barrels_remove: function(frm) {
        update_weight_totals(frm);
    }
});

// Level settings and UI
function configure_level_settings(frm) {
    const level = frm.doc.custom_batch_level;
    switch(level) {
        case '1':
            frm.set_value('parent_batch_amb', '');
            frm.set_value('is_group', 1);
            break;
        case '2':
            frm.set_value('is_group', 1);
            break;
        case '3':
            frm.set_value('is_group', 1);
            initialize_barrel_management(frm);
            break;
        case '4':
            frm.set_value('is_group', 0);
            break;
        default:
            frm.set_value('is_group', 0);
    }
}

function add_level_specific_buttons(frm) {
    frm.custom_buttons = {};
    if (frm.is_new()) return;

    switch(frm.doc.custom_batch_level) {
        case '1':
            frm.add_custom_button(__('Create Sublot'), function() {
                create_sublot_batch(frm);
            });
            break;
        case '2':
            frm.add_custom_button(__('Create Container'), function() {
                create_container_batch(frm);
            });
            break;
        case '3':
            frm.add_custom_button(__('Scan Multiple Barcodes'), function() {
                open_bulk_scan_dialog(frm);
            });
            frm.add_custom_button(__('Generate Barrel Serials'), function() {
                generate_bulk_barrel_serials(frm);
            });
            frm.add_custom_button(__('Validate All Weights'), function() {
                validate_all_barrel_weights(frm);
            });
            break;
    }
}

// Barrel management
function initialize_barrel_management(frm) {
    setTimeout(function() {
        update_weight_totals(frm);
    }, 500);
}

function process_quick_barcode_scan(frm) {
    const barcode = frm.doc.quick_barcode_scan;
    if (!validate_code39_format(barcode)) {
        frappe.msgprint('Invalid CODE-39 barcode format');
        frm.set_value('quick_barcode_scan', '');
        return;
    }
    if (frm.doc.custom_batch_level == '3') {
            calculate_container_totals(frm);
    }
    if (check_duplicate_serial(frm, barcode)) {
        frappe.msgprint('Barrel serial number already exists: ' + barcode);
        frm.set_value('quick_barcode_scan', '');
        return;
    }
    const row = frm.add_child('container_barrels');
    row.barrel_serial_number = barcode;
    row.packaging_type = frm.doc.default_packaging_type;
    row.scan_timestamp = frappe.datetime.now_datetime();
    if (row.packaging_type) {
        fetch_tara_weight_for_row(frm, row.doctype, row.name);
    }
    frm.refresh_field('container_barrels');
    frm.set_value('quick_barcode_scan', '');
    setTimeout(function() { frm.scroll_to_field('container_barrels'); }, 300);
}

function process_barcode_scan(frm, cdt, cdn, barcode) {
    const row = locals[cdt][cdn];
    if (!validate_code39_format(barcode)) {
        frappe.msgprint('Invalid CODE-39 barcode format');
        frappe.model.set_value(cdt, cdn, 'barcode_scan_input', '');
        return;
    }
    frappe.model.set_value(cdt, cdn, 'barrel_serial_number', barcode);
    frappe.model.set_value(cdt, cdn, 'scan_timestamp', frappe.datetime.now_datetime());
    frappe.model.set_value(cdt, cdn, 'barcode_scan_input', '');
    if (row.packaging_type) {
        fetch_tara_weight_for_row(frm, cdt, cdn);
    }
}

function generate_barrel_serial_number(frm, row) {
    const container_code = frm.doc.title || frm.doc.custom_generated_batch_name;
    if (!container_code) return;

    let max_seq = 0;
    (frm.doc.container_barrels || []).forEach(barrel => {
        if (barrel.barrel_serial_number && barrel.barrel_serial_number.startsWith(container_code)) {
            const match = barrel.barrel_serial_number.match(/-([0-9]+)$/);
            if (match) {
                max_seq = Math.max(max_seq, parseInt(match[1], 10));
            }
        }
    });

    const next_seq = (max_seq + 1).toString().padStart(3, '0');
    row.barrel_serial_number = `${container_code}-${next_seq}`;
}

function fetch_tara_weight_for_row(frm, cdt, cdn) {
    const row = locals[cdt][cdn];
    if (!row.packaging_type) return;

    frappe.call({
        method: 'frappe.client.get',
        args: {
            doctype: 'Item',
            name: row.packaging_type
        },
        callback: function(r) {
            if (r.message && r.message.weight_per_unit) {
                frappe.model.set_value(cdt, cdn, 'tara_weight', r.message.weight_per_unit);
                calculate_net_weight(frm, cdt, cdn);
            }
        }
    });
}

function calculate_net_weight(frm, cdt, cdn) {
    const row = locals[cdt][cdn];
    if (row.gross_weight && row.tara_weight) {
        const net_weight = row.gross_weight - row.tara_weight;
        frappe.model.set_value(cdt, cdn, 'net_weight', net_weight);
        frappe.model.set_value(cdt, cdn, 'weight_validated', net_weight > 0 && net_weight < row.gross_weight ? 1 : 0);
    }
}

function update_weight_totals(frm) {
    if (frm.doc.custom_batch_level != '3' || !frm.doc.container_barrels) return;

    let total_gross = 0, total_tara = 0, total_net = 0, barrel_count = 0;
    frm.doc.container_barrels.forEach(row => {
        if (row.gross_weight) total_gross += row.gross_weight;
        if (row.tara_weight) total_tara += row.tara_weight;
        if (row.net_weight) total_net += row.net_weight;
        if (row.barrel_serial_number) barrel_count += 1;
    });
    frm.set_value('total_gross_weight', total_gross);
    frm.set_value('total_tara_weight', total_tara);
    frm.set_value('total_net_weight', total_net);
    frm.set_value('barrel_count', barrel_count);
}

// Validation helpers
function validate_code39_format(barcode) {
    const s = String(barcode || '').toUpperCase();
    // Allowed: A-Z 0-9 and - . space $ / + % *
    return /^[A-Z0-9\-\.\s$\/+%*]+$/.test(s);
}

function check_duplicate_serial(frm, serial) {
    if (!frm.doc.container_barrels) return false;
    return frm.doc.container_barrels.some(row => row.barrel_serial_number === serial);
}

function validate_barrel_data(frm) {
    if (!frm.doc.container_barrels) return true;
    let has_errors = false;

    frm.doc.container_barrels.forEach((row, index) => {
        if (row.barrel_serial_number && !row.gross_weight) {
            frappe.msgprint(`Row ${index + 1}: Gross weight is required for barrel ${row.barrel_serial_number}`);
            has_errors = true;
        }
        if (row.gross_weight && row.tara_weight && row.net_weight <= 0) {
            frappe.msgprint(`Row ${index + 1}: Net weight cannot be zero or negative for barrel ${row.barrel_serial_number}`);
            has_errors = true;
        }
    });

    if (has_errors) {
        frappe.throw('Please fix barrel weight validation errors before saving.');
    }
    return true;
}

// Bulk ops
function open_bulk_scan_dialog(frm) {
    const dialog = new frappe.ui.Dialog({
        title: 'Bulk Barcode Scanning',
        fields: [
            { fieldtype: 'Small Text', fieldname: 'barcode_list', label: 'Scan Multiple Barcodes (one per line)', description: 'Scan or paste CODE-39 barcodes, one per line' },
            { fieldtype: 'Link', fieldname: 'bulk_packaging_type', label: 'Default Packaging Type', options: 'Item', reqd: 1 }
        ],
        primary_action_label: 'Add Barrels',
        primary_action: function(values) {
            process_bulk_barcodes(frm, values.barcode_list, values.bulk_packaging_type);
            dialog.hide();
        }
    });
    if (frm.doc.default_packaging_type) {
        dialog.set_value('bulk_packaging_type', frm.doc.default_packaging_type);
    }
    dialog.show();
}

function process_bulk_barcodes(frm, barcode_text, packaging_type) {
    const barcodes = barcode_text.split('\n').filter(b => b.trim());
    let added_count = 0;

    barcodes.forEach(b => {
        const barcode = b.trim();
        if (!validate_code39_format(barcode)) {
            frappe.msgprint(`Invalid barcode format: ${barcode}`);
            return;
        }
        if (check_duplicate_serial(frm, barcode)) {
            frappe.msgprint(`Duplicate barcode: ${barcode}`);
            return;
        }
        const row = frm.add_child('container_barrels');
        row.barrel_serial_number = barcode;
        row.packaging_type = packaging_type;
        row.scan_timestamp = frappe.datetime.now_datetime();
        added_count += 1;
    });

    frm.refresh_field('container_barrels');
    if (added_count > 0) {
        frappe.msgprint(`Added ${added_count} barrels successfully`);
        fetch_tara_weights_for_all_rows(frm, packaging_type);
    }
}

function fetch_tara_weights_for_all_rows(frm, packaging_type) {
    frappe.call({
        method: 'frappe.client.get',
        args: { doctype: 'Item', name: packaging_type },
        callback: function(r) {
            if (r.message && r.message.weight_per_unit) {
                const tara_weight = r.message.weight_per_unit;
                (frm.doc.container_barrels || []).forEach(row => {
                    if (row.packaging_type === packaging_type && !row.tara_weight) {
                        frappe.model.set_value('Container Barrels', row.name, 'tara_weight', tara_weight);
                    }
                });
                frm.refresh_field('container_barrels');
            }
        }
    });
}

function generate_bulk_barrel_serials(frm) {
    const dialog = new frappe.ui.Dialog({
