// Get next sequence number from TDS Settings
function get_next_sequence(frm) {
    if (!frm.doc.item_code) return;
    
    frappe.call({
        method: 'frappe.client.get_list',
        args: {
            doctype: 'TDS Settings',
            filters: { item_code: frm.doc.item_code },
            fields: ['name', 'last_sequence_used']
        },
        callback: function(r) {
            if (r.message && r.message.length > 0) {
                // Existing record found
                const settings = r.message[0];
                const next_seq = (settings.last_sequence_used || 0) + 1;
                frm.set_value('tds_sequence', next_seq);
                update_sequence_in_settings(settings.name, next_seq);
            } else {
                // Create new TDS Settings record
                frappe.call({
                    method: 'frappe.client.insert',
                    args: {
                        doc: {
                            doctype: 'TDS Settings',
                            item_code: frm.doc.item_code,
                            last_sequence_used: 0
                        }
                    },
                    callback: function(r) {
                        frm.set_value('tds_sequence', 1);
                        update_sequence_in_settings(r.message.name, 1);
                    }
                });
            }
        }
    });
}

// Update sequence in TDS Settings
function update_sequence_in_settings(name, sequence) {
    frappe.call({
        method: 'frappe.client.set_value',
        args: {
            doctype: 'TDS Settings',
            name: name,
            fieldname: { last_sequence_used: sequence }
        }
    });
}

// Generate final version number
function generate_version(frm) {
    if (!validate_required_fields(frm)) return;
    
    const date_parts = frm.doc.approval_date.split('-');
    const version = `V1.${frm.doc.item_code}-Date:${date_parts[0].slice(-2)}/${date_parts[1]}/${date_parts[2]}*${String(frm.doc.tds_sequence).padStart(4, '0')}`;
    
    frm.set_value('tds_version', version);
    frappe.show_alert(__('Version generated successfully'));
}

// Validate required fields
function validate_required_fields(frm) {
    let valid = true;
    
    if (!frm.doc.item_code) {
        frappe.msgprint(__('Item Code is required'));
        valid = false;
    }
    if (!frm.doc.approval_date) {
        frappe.msgprint(__('Approval Date is required'));
        valid = false;
    }
    if (!frm.doc.tds_sequence) {
        frappe.msgprint(__('Sequence Number is required'));
        valid = false;
    }
    
    return valid;
}

// Update version preview
function update_preview(frm) {
    if (frm.doc.item_code && frm.doc.approval_date && frm.doc.tds_sequence) {
        const date_parts = frm.doc.approval_date.split('-');
        const preview = `Preview: V1.${frm.doc.item_code}-Date:${date_parts[0].slice(-2)}/${date_parts[1]}/${date_parts[2]}*${String(frm.doc.tds_sequence).padStart(4, '0')}`;
        
        if (!frm.fields_dict.version_preview) {
            frm.add_custom_field('version_preview', {
                fieldtype: 'Section Break',
                label: 'Version Preview',
                description: preview
            });
        } else {
            frm.set_df_property('version_preview', 'description', preview);
        }
    }
}
