frappe.ui.form.on('TDS Settings', {
    refresh: function(frm) {
        frm.add_custom_button(__('Generate Version'), function() {
            // Validate required fields exist
            if (!frm.doc.naming_series) {
                frappe.msgprint(__('Naming Series is required'));
                return;
            }
            if (!frm.doc.tds_version_counter) {
                frappe.msgprint(__('Version Counter is required'));
                return;
            }
            if (!frm.doc.item_code) {
                frappe.msgprint(__('Item Code is required'));
                return;
            }
            if (frm.doc.tds_series_counter === undefined || frm.doc.tds_series_counter === null) {
                frappe.msgprint(__('Series Counter is required'));
                return;
            }

            // Extract all digits from naming series (e.g., "1707" from "TDS-1707")
            const allDigits = frm.doc.name.replace(/\D/g, '');
            
            // Validate we have at least 4 digits
            if (allDigits.length < 4) {
                frappe.msgprint(__('Naming Series must contain at least 4 digits', allDigits));
                return;
            }

            // Get last 4 digits
            const last_four = allDigits.slice(-4);

            // Generate current date components
