            grid_row.$row.addClass('result-pass');
        } else if (row.result_status === 'FAIL') {
            grid_row.$row.addClass('result-fail');
        } else if (row.result_status === 'N/A') {
            grid_row.$row.addClass('result-na');
        }
    }
}

// Function to validate a single row
function validate_parameter_row(row) {
    if (!row.result_value) {
        return null;
    }

    if (row.is_numeric == 1) {
        // Numeric validation
        const result = parseFloat(row.result_value);
        
        if (isNaN(result)) {
            frappe.model.set_value(row.doctype, row.name, 'result_status', 'FAIL');
            return false;
        }

        const min = parseFloat(row.min_value) || -Infinity;
        const max = parseFloat(row.max_value) || Infinity;

        if (result >= min && result <= max) {
            frappe.model.set_value(row.doctype, row.name, 'result_status', 'PASS');
            return true;
        } else {
            frappe.model.set_value(row.doctype, row.name, 'result_status', 'FAIL');
            return false;
        }
    } else {
        // Non-numeric validation
        const result = (row.result_value || '').toString().toUpperCase().trim();
        
        if (result === 'PASS' || result === 'YES' || result === 'TRUE' || result === 'OK') {
            frappe.model.set_value(row.doctype, row.name, 'result_status', 'PASS');
            return true;
        } else if (result === 'FAIL' || result === 'NO' || result === 'FALSE' || result === 'NOT OK') {
            frappe.model.set_value(row.doctype, row.name, 'result_status', 'FAIL');
            return false;
        } else {
            frappe.model.set_value(row.doctype, row.name, 'result_status', 'N/A');
            return null;
        }
    }
}

// Function to validate all results in the table
function validate_all_results(frm) {
    if (!frm.doc.coa_quality_test_parameter || frm.doc.coa_quality_test_parameter.length === 0) {
        frappe.msgprint(__('No test parameters found'));
        return;
    }

    let allValid = true;
    let passedCount = 0;
    let failedCount = 0;
    let naCount = 0;
    let emptyCount = 0;

    frm.doc.coa_quality_test_parameter.forEach(function(row) {
        if (row.result_value) {
            const isValid = validate_parameter_row(row);
            if (isValid === true) passedCount++;
            if (isValid === false) {
                failedCount++;
                allValid = false;
            }
            if (isValid === null) naCount++;
        } else {
            emptyCount++;
        }
    });

    // Refresh the table to show updated status
    frm.refresh_field('coa_quality_test_parameter');

    // Show summary
    let message = __(`Validation complete: ${passedCount} PASS, ${failedCount} FAIL`);
    if (naCount > 0) message += __(`, ${naCount} N/A`);
    if (emptyCount > 0) message += __(`, ${emptyCount} Empty`);

    frappe.msgprint({
        title: __('Validation Results'),
        indicator: allValid ? 'green' : 'red',
        message: message
    });

    return allValid;
}

