// OPTION 1: Client-side processing (PREFERRED)
async function load_with_client_side(frm) {
    const result = await frappe.call({
        method: 'frappe.client.get',
        args: {
            doctype: 'TDS Product Specification',
            name: frm.doc.linked_tds,
            fields: [
                'item_quality_inspection_parameter.parameter',
                'item_quality_inspection_parameter.specification',
                'item_quality_inspection_parameter.min_value',
                'item_quality_inspection_parameter.max_value'
            ]
        }
    });

    if (result.message) {
        process_parameters_client_side(result.message, frm);
    }
}

// OPTION 2: Server script approach
async function load_with_server_script(frm) {
    const result = await frappe.call({
        method: 'load_tds_parameters',
        args: {
            coa_name: frm.doc.name || 'new_document',
            tds_name: frm.doc.linked_tds
        }
    });

    if (result.message && result.message.success) {
        process_server_response(result.message, frm);
    } else {
        throw new Error(result.message?.message || 'Server script failed');
    }
}

function process_parameters_client_side(tds_doc, frm) {
    if (!tds_doc.item_quality_inspection_parameter) {
        throw new Error('No parameters found in TDS');
    }

    frm.clear_table('coa_quality_test_parameter');
    
    tds_doc.item_quality_inspection_parameter.forEach(param => {
        const row = frm.add_child('coa_quality_test_parameter');
        Object.assign(row, {
            parameter: param.parameter || 'Parameter',
            specification: param.specification || '',
            min_value: param.min_value,
            max_value: param.max_value,
            is_numeric: (param.min_value || param.max_value) ? 1 : 0,
            result_status: 'Pending'
        });
    });

    frm.refresh_field('coa_quality_test_parameter');
    frappe.show_alert(__('Successfully loaded parameters'), 'green');
}

function process_server_response(response, frm) {
    if (response.parameters) {
        frm.clear_table('coa_quality_test_parameter');
        
        response.parameters.forEach(param => {
            const row = frm.add_child('coa_quality_test_parameter');
            Object.assign(row, param);
        });

        frm.refresh_field('coa_quality_test_parameter');
    }
    
    frappe.show_alert(response.message, 'green');
}

function handle_load_error(error) {
    console.error('Load error:', error);
    frappe.msgprint({
        title: __('Load Failed'),
        message: __('Error loading parameters: ') + error.message,
        indicator: 'red'
    });
}

