function generate_batch_code(frm) {
    const level = parseInt(frm.doc.custom_batch_level) || 1;
    console.log('Generating batch code for level:', level);
    
    if (level === 1) {
        // Generate base batch code for Level 1
        generate_level_1_batch_code(frm);
    } else {
        // Generate hierarchical batch code for Level 2+
        generate_sublot_batch_code(frm, level);
    }
}

function generate_level_1_batch_code(frm) {
    // Get base components from work order and form data
    get_base_components(frm, function(components) {
        console.log('Base components:', components);
        
        const product_code = components.product_code;
        const consecutive = components.consecutive.toString().padStart(5, '0');
        const plant_code = components.plant_code;
        
        // Generate final batch code: PRODUCTCODE + CONSECUTIVE + PLANTCODE
        const final_batch_code = `${product_code}${consecutive}${plant_code}`;
        
        console.log('Final batch code generated:', final_batch_code);
        
        // Set the generated batch code
        frm.set_value('title', final_batch_code);
        frm.set_value('custom_generated_batch_name', final_batch_code);
        
        // Store business data in custom fields
        frm.set_value('custom_consecutive_number', consecutive);
        frm.set_value('custom_plant_code', plant_code);
        
        frm.refresh_field('title');
        frm.refresh_field('custom_generated_batch_name');
    });
}

function generate_sublot_batch_code(frm, level) {
    if (!frm.doc.parent_batch_amb) {
        console.log('No parent selected for level', level);
        return;
    }
    
    // Prevent self-referencing
    if (frm.doc.parent_batch_amb === frm.doc.name) {
        frappe.msgprint('A batch cannot be its own parent');
        frm.set_value('parent_batch_amb', '');
        return;
    }
    
    // For Level 2+, get the parent's batch name and append sublot number
    frappe.call({
        method: 'frappe.client.get',
        args: {
            doctype: 'Batch AMB',
            name: frm.doc.parent_batch_amb
        },
        callback: function(r) {
            if (r.message) {
                const parent_batch = r.message;
                const parent_batch_code = parent_batch.title || parent_batch.custom_generated_batch_name;
                
                if (!parent_batch_code) {
                    frappe.msgprint('Parent batch does not have a valid batch code');
                    return;
                }
                
                console.log('Parent batch code:', parent_batch_code);
                
                // Get next consecutive number for this sublot
                get_next_sublot_consecutive(frm, parent_batch_code, function(next_consecutive) {
                    console.log('Next consecutive for level', level, ':', next_consecutive);
                    
                    // Generate sublot code: PARENT_CODE-CONSECUTIVE
                    const final_batch_code = `${parent_batch_code}-${next_consecutive}`;
                    
                    console.log('Final batch code generated:', final_batch_code);
                    
                    // Set the generated batch code
                    frm.set_value('title', final_batch_code);
                    frm.set_value('custom_generated_batch_name', final_batch_code);
                    
                    // Store sublot consecutive number in custom field
                    frm.set_value('custom_sublot_consecutive', next_consecutive);
                    
                    frm.refresh_field('title');
                    frm.refresh_field('custom_generated_batch_name');
                });
            } else {
                frappe.msgprint('Could not fetch parent batch information');
            }
        }
    });
}

function get_base_components(frm, callback) {
    // Extract product code from work order or item
    frappe.call({
        method: 'frappe.client.get',
        args: {
            doctype: 'Work Order',
            name: frm.doc.work_order_ref
        },
        callback: function(r) {
            if (r.message) {
                const wo = r.message;
                
                // Extract product code (first 4 digits from item code or name)
                let product_code = '';
                if (wo.production_item) {
                    const match = wo.production_item.match(/^(\d{4})/);
                    product_code = match ? match[1] : '0000';
                } else {
                    product_code = '0000';
                }
                
                // Get consecutive number from work order name
                let consecutive = 1;
                const wo_match = wo.name.match(/(\d+)$/);
                if (wo_match) {
                    consecutive = parseInt(wo_match[1]);
                }
                
                // Get year and plant code from form
                const year = frm.doc.custom_batch_year || '25';
                const plant_code = frm.doc.custom_plant_code || '1';
                
                callback({
                    product_code: product_code,
                    consecutive: consecutive,
                    year: year,
                    plant_code: plant_code,
                    source: 'work_order'
                });
            } else {
                // Fallback values
                callback({
                    product_code: '0000',
                    consecutive: 1,
                    year: '25',
                    plant_code: '1',
                    source: 'fallback'
                });
            }
        }
    });
}

function get_next_sublot_consecutive(frm, parent_batch_code, callback) {
    // Find existing sublots for this parent
    frappe.call({
        method: 'frappe.client.get_list',
        args: {
            doctype: 'Batch AMB',
            filters: {
                'parent_batch_amb': frm.doc.parent_batch_amb,
                'name': ['!=', frm.doc.name || '']  // Exclude current document if editing
            },
            fields: ['name', 'title', 'custom_generated_batch_name'],
            limit_page_length: 100
        },
        callback: function(r) {
            let next_consecutive = 1;
            
            if (r.message && r.message.length > 0) {
                // Extract consecutive numbers from existing sublots
                const existing_consecutives = [];
                
                r.message.forEach(function(batch) {
                    const batch_name = batch.title || batch.custom_generated_batch_name || '';
                    
                    // Look for pattern: PARENT_CODE-NUMBER
                    const pattern = new RegExp(`^${parent_batch_code.replace(/[.*+?^${}()|[\]\\]/g, '\\$&')}-(\\d+)$`);
                    const match = batch_name.match(pattern);
                    
                    if (match) {
                        existing_consecutives.push(parseInt(match[1]));
                    }
                });
                
                // Find the next available consecutive number
                if (existing_consecutives.length > 0) {
                    next_consecutive = Math.max(...existing_consecutives) + 1;
                }
            }
            
            callback(next_consecutive);
        }
    });
}

