function makeWidgetDraggable(widget) {
    let isDragging = false;
    let startX, startY, initialTop, initialLeft;
    
    widget.find('h4').css('cursor', 'move');
    
    widget.find('h4').on('mousedown', function(e) {
        isDragging = true;
        startX = e.clientX;
        startY = e.clientY;
        initialTop = parseInt(widget.css('top'));
        initialLeft = parseInt(widget.css('right'));
        widget.css('cursor', 'move');
        e.preventDefault();
    });
    
    $(document).on('mousemove', function(e) {
        if (!isDragging) return;
        
        const newRight = initialLeft - (e.clientX - startX);
        const newTop = initialTop + (e.clientY - startY);
        
        widget.css({
            right: newRight + 'px',
            top: newTop + 'px'
        });
    });
    
    $(document).on('mouseup', function() {
        isDragging = false;
        widget.css('cursor', 'default');
