            // Use setTimeout to allow the field change to be processed normally
            setTimeout(function() {
                perform_real_time_validation_for_row(frm, cdt, cdn);
            }, 200);
        }
    });
    
    console.log('Real-time validation setup complete');
}

function perform_real_time_validation_for_row(frm, cdt, cdn) {
    console.log('Performing real-time validation for row ' + cdn);
    
    var row = locals[cdt][cdn];
    if (!row) {
        console.log('Row not found');
        return;
    }
    
    console.log('  Current result value: "' + row.result_value + '"');
    
    // Skip validation if result_value is empty
    if (!row.result_value || row.result_value.toString().trim() === '') {
        // Clear any existing validation styling
        clear_validation_styling_fixed(cdn);
        // Clear result text
        frappe.model.set_value(cdt, cdn, 'result_text', '');
        return;
    }
    
    // Handle negative values in real-time validation
    var validation_result = validate_single_parameter(row);
    
    // Apply styling based on validation result
    apply_validation_styling_fixed(cdn, validation_result);
    
    // Update result_text field with PASS/FAIL status
    update_result_text_field(frm, cdt, cdn, validation_result.status);
    
    console.log('  Validation result: ' + validation_result.status);
}

function update_result_text_field(frm, cdt, cdn, status) {
    // Update the result_text field with the validation status
    frappe.model.set_value(cdt, cdn, 'result_text', status);
    
    // Also update the result_status field if it exists
    if (locals[cdt][cdn].result_status !== undefined) {
        var status_display = '';
        switch(status) {
            case 'PASS': status_display = 'Compliant'; break;
            case 'FAIL': status_display = 'Non-Compliant'; break;
            case 'PENDING': status_display = 'Pending'; break;
            default: status_display = status;
        }
        frappe.model.set_value(cdt, cdn, 'result_status', status_display);
    }
}

function clear_validation_styling_fixed(cdn) {
    try {
        // Target the specific row by data-name attribute
        var row_element = $('div[data-name="' + cdn + '"]');
        if (row_element.length > 0) {
            // Remove validation classes from the result_value field input
            row_element.find('[data-fieldname="result_value"] input').removeClass('validation-pass validation-fail');
            
            // Remove any validation indicator elements we might have added
            row_element.find('.validation-indicator').remove();
        }
    } catch (error) {
        console.log('Error clearing validation styling:', error);
    }
}

function apply_validation_styling_fixed(cdn, validation_result) {
    try {
        // Target the specific row by data-name attribute
        var row_element = $('div[data-name="' + cdn + '"]');
        if (row_element.length === 0) {
            console.log('Row element not found for cdn:', cdn);
            return;
        }
        
        // Find the result_value input field specifically
        var result_input = row_element.find('[data-fieldname="result_value"] input');
        if (result_input.length === 0) {
            console.log('Result input field not found for cdn:', cdn);
            return;
        }
        
        // Clear existing validation classes first
        result_input.removeClass('validation-pass validation-fail');
        
        // Apply appropriate class based on validation result
        if (validation_result.status === 'PASS') {
            result_input.addClass('validation-pass');
        } else if (validation_result.status === 'FAIL') {
            result_input.addClass('validation-fail');
        }
        
        console.log('Applied validation styling for row ' + cdn + ': ' + validation_result.status);
        
    } catch (error) {
        console.log('Error applying validation styling:', error);
    }
}

// Also update the single parameter validation function
function validate_single_parameter(param) {
    var result = {
        status: 'PENDING',
        message: '',
        parameter_name: param.parameter || 'Unknown'
    };
    
    // Skip validation for title rows
    if (is_title_row(param)) {
        result.status = 'SKIP';
        result.message = 'Title row - validation skipped';
        return result;
    }
    
    // Skip if result_value is empty
    if (!param.result_value || param.result_value.toString().trim() === '') {
        result.status = 'PENDING';
        result.message = 'No result value entered';
        return result;
    }
    
    try {
        var resultValue = parseFloat(param.result_value);
        var minValue = param.min_value !== null && param.min_value !== undefined && param.min_value !== '' ? parseFloat(param.min_value) : null;
        var maxValue = param.max_value !== null && param.max_value !== undefined && param.max_value !== '' ? parseFloat(param.max_value) : null;
        
        // Get Acceptance Criteria Value (this is the 'value' field, not 'specification')
        var acceptanceCriteria = param.value || '';
        
        // SPECIAL CASE: Handle "NEGATIVE" acceptance criteria
        if (acceptanceCriteria.toUpperCase().includes('NEGATIVE')) {
            var resultStr = param.result_value.toString().toUpperCase().trim();
            if (resultStr === 'NEGATIVE' || resultStr === 'NEG' || resultStr === '0') {
                result.status = 'PASS';
                result.message = 'Negative result as required';
            } else {
                result.status = 'FAIL';
                result.message = 'Expected negative result but got "' + param.result_value + '"';
            }
            return result;
        }
        
        // SPECIAL CASE: Handle "NOT DETECTABLE" acceptance criteria
        if (acceptanceCriteria.toUpperCase().includes('NOT DETECTABLE')) {
            var lodMatch = acceptanceCriteria.match(/LOD\s*([0-9.]+)/i);
            var lodValue = lodMatch ? parseFloat(lodMatch[1]) : 0;
            
            if (!isNaN(resultValue) && resultValue < lodValue) {
                result.status = 'PASS';
                result.message = 'Value below detection limit of ' + lodValue;
            } else {
                result.status = 'FAIL';
                result.message = 'Value "' + param.result_value + '" should be below LOD of ' + lodValue;
            }
            return result;
        }
        
        // Check if result value is a valid number
        if (isNaN(resultValue)) {
            // For non-numeric values, check against acceptance criteria
            if (acceptanceCriteria && acceptanceCriteria.trim() !== '') {
                // Compare result with acceptance criteria (case-insensitive)
                var resultStr = param.result_value.toString().toLowerCase().trim();
                var criteriaStr = acceptanceCriteria.toString().toLowerCase().trim();
                
                if (resultStr === criteriaStr) {
                    result.status = 'PASS';
                    result.message = 'Value matches acceptance criteria';
                } else {
                    result.status = 'FAIL';
                    result.message = 'Value doesn\'t match acceptance criteria';
                }
            } else {
                // No acceptance criteria to compare against - auto-pass if value exists
                result.status = 'PASS';
                result.message = 'No acceptance criteria, value exists';
            }
            return result;
        }
        
        // Handle special case where both min and max are 0
        if (minValue === 0 && maxValue === 0) {
            if (resultValue === 0) {
                result.status = 'PASS';
                result.message = 'Value is exactly 0 as required';
            } else {
                result.status = 'FAIL';
                result.message = 'Value ' + resultValue + ' is not 0 (required range: 0 - 0)';
            }
            return result;
        }
        
        // Perform range validation for numeric values
        var withinMin = (minValue === null || isNaN(minValue) || resultValue >= minValue);
        var withinMax = (maxValue === null || isNaN(maxValue) || resultValue <= maxValue);
        
        if (withinMin && withinMax) {
            result.status = 'PASS';
            result.message = 'Value ' + resultValue + ' is within acceptable range';
        } else {
            result.status = 'FAIL';
            var rangeInfo = '';
            if (minValue !== null && !isNaN(minValue)) rangeInfo += 'Min: ' + minValue;
            if (maxValue !== null && !isNaN(maxValue)) {
                if (rangeInfo) rangeInfo += ', ';
                rangeInfo += 'Max: ' + maxValue;
            }
            result.message = 'Value ' + resultValue + ' is outside acceptable range (' + rangeInfo + ')';
        }
        
    } catch (error) {
        result.status = 'ERROR';
        result.message = 'Validation error: ' + error.message;
        console.error('Validation error for parameter ' + param.parameter + ':', error);
    }
    
    return result;
}
