    }
        if (acceptanceCriteria.toUpperCase().includes('NONE')) {
        var resultStr = resultValue.toString().toUpperCase().trim();
        if (resultStr === 'NONE' || resultStr === '0' ) {
            result.status = 'PASS';
            console.log('  Status: PASS (negative test result)');
        } else {
            result.status = 'FAIL';
            result.issues.push('Row ' + (index + 1) + ': Expected negative result but got "' + resultValue + '"');
            console.log('  Status: FAIL (not negative)');
        }
        return result;
    }
    // SPECIAL CASE: Handle "NOT DETECTABLE" specifications
    if (acceptanceCriteria.toUpperCase().includes('NOT DETECTABLE')) {
        var resultNum = parseFloat(resultValue);
        var lodMatch = acceptanceCriteria.match(/LOD\s*([0-9.]+)/i);
        var lodValue = lodMatch ? parseFloat(lodMatch[1]) : 0;
        
        if (!isNaN(resultNum) && resultNum < lodValue) {
            result.status = 'PASS';
            console.log('  Status: PASS (below detection limit)');
        } else {
            result.status = 'FAIL';
            result.issues.push('Row ' + (index + 1) + ': Value "' + resultValue + '" should be below LOD of ' + lodValue);
            console.log('  Status: FAIL (above detection limit)');
        }
        return result;
    }
    
    // Determine if this should be treated as numeric
    var minVal = param.min_value;
    var maxVal = param.max_value;
    var hasNumericConstraints = (minVal !== null && minVal !== undefined && minVal !== '') || 
                                  (maxVal !== null && maxVal !== undefined && maxVal !== '');
    
    console.log('  Min: ' + minVal + ', Max: : ' + maxVal + ', Has numeric constraints: ' + hasNumericConstraints);
    
    if (hasNumericConstraints) {
        // NUMERIC VALIDATION (including negative values)
        var resultNum = parseFloat(resultValue);
        var minNum = minVal !== null && minVal !== undefined && minVal !== '' ? parseFloat(minVal) : null;
        var maxNum = maxVal !== null && maxVal !== undefined && maxVal !== '' ? parseFloat(maxVal) : null;
        
        console.log('  Parsed values - Result: ' + resultNum + ', Min: ' + minNum + ', Max: ' + maxNum);
        
        // Check if result is a valid number (including negative values)
        if (isNaN(resultNum)) {
            result.status = 'FAIL';
            result.issues.push('Row ' + (index + 1) + ': Invalid numeric result value "' + resultValue + '"');
            console.log('  Status: FAIL (invalid number)');
            return result;
        }
        
        // SPECIAL CASE: Handle "must be exactly 0" (both min and max are 0)
        if (minNum === 0 && maxNum === 0) {
            if (resultNum === 0) {
                result.status = 'PASS';
                console.log('  Status: PASS (exactly 0 as required)');
            } else {
                result.status = 'FAIL';
                result.issues.push('Row ' + (index + 1) + ': Value ' + resultNum + ' is not 0 (required range: 0 - 0)');
                console.log('  Status: FAIL (not 0 when range is 0-0)');
            }
            return result;
        }
        
        // Check range constraints (handles negative values correctly)
        var withinRange = true;
        var constraintMessages = [];
        
        // Check minimum value (handles negative values)
        if (minVal !== null && minVal !== undefined && minVal !== '' && !isNaN(minNum)) {
            if (resultNum < minNum) {
                withinRange = false;
                constraintMessages.push('Result ' + resultNum + ' below minimum ' + minNum);
            }
        }
        
        // Check maximum value (handles negative values)
        if (maxVal !== null && maxVal !== undefined && maxVal !== '' && !isNaN(maxNum)) {
            if (resultNum > maxNum) {
                withinRange = false;
                constraintMessages.push('Result ' + resultNum + ' above maximum ' + maxNum);
            }
        }
        
        if (withinRange) {
            result.status = 'PASS';
            console.log('  Status: PASS (within range)');
        } else {
            result.status = 'FAIL';
            result.issues.push('Row ' + (index + 1) + ': ' + constraintMessages.join(', '));
            console.log('  Status: FAIL (' + constraintMessages.join(', ') + ')');
        }
        
    } else {
        // NON-NUMERIC VALIDATION - Check against acceptance criteria
        console.log('  Acceptance Criteria: "' + acceptanceCriteria + '"');
        
        if (acceptanceCriteria && acceptanceCriteria.trim() !== '') {
            // Compare result with acceptance criteria (case-insensitive)
            var resultStr = resultValue.toString().toLowerCase().trim();
            var criteriaStr = acceptanceCriteria.toString().toLowerCase().trim();
            
            console.log('  Comparing: "' + resultStr + '" vs "' + criteriaStr + '"');
            
            if (resultStr === criteriaStr) {
                result.status = 'PASS';
                console.log('  Status: PASS (matches acceptance criteria)');
            } else {
                result.status = 'FAIL';
                result.issues.push('Row ' + (index + 1) + ': Result "' + resultValue + '" doesn\'t match acceptance criteria "' + acceptanceCriteria + '"');
                console.log('  Status: FAIL (doesn\'t match acceptance criteria)');
            }
        } else {
            // No acceptance criteria to compare against - auto-pass if value exists
            result.status = 'PASS';
            console.log('  Status: PASS (no acceptance criteria, value exists)');
        }
    }
    
    return result;
}

// Enhanced function to check if a row is a title/header
function is_title_row(param) {
    if (!param) return false;
    
    // Check explicit title row flag from TDS
    if (param.is_title_row === 1) {
        return true;
    }
    
    // Check if parameter name suggests it's a title
    if (param.parameter) {
        var paramLower = param.parameter.toLowerCase();
        if (paramLower.includes('title') || 
            paramLower.includes('header') || 
            paramLower.includes('section') ||
            paramLower === 'organoleptic' ||
            paramLower === 'physicochemical' ||
            paramLower === 'microbiological' ||
            paramLower === 'other analysis' ||
            paramLower.includes('---') ||
            paramLower.includes('***') ||
            paramLower.includes('===')) {
            return true;
        }
    }
    
    // Check if it has no validation criteria (no min/max/specification)
    var hasMinMax = (param.min_value !== null && param.min_value !== undefined && param.min_value !== '') ||
                      (param.max_value !== null && param.max_value !== undefined && param.max_value !== '');
    var hasSpec = param.specification && param.specification.trim() !== '';
    
    if (!hasMinMax && !hasSpec) {
        return true;
    }
    
    return false;
}

function update_document_validation_status(frm, validation_results) {
    var validation_status = 'Non-Compliant';
    var overall_status = 'FAIL';
    
    if (validation_results.failed === 0 && validation_results.passed > 0) {
        validation_status = 'Compliant';
        overall_status = 'PASS';
    } else if (validation_results.pending > 0 && validation_results.failed === 0) {
        validation_status = 'Pending Review';
        overall_status = 'PENDING';
    } else if (validation_results.passed === 0 && validation_results.failed === 0 && validation_results.pending === 0) {
        validation_status = 'Not Started';
        overall_status = 'PENDING';
    }
    
    console.log('Setting validation status to:', validation_status);
    
    // Update the document fields - check if fields exist first
    if (frm.fields_dict.validation_status) {
        frm.set_value('validation_status', validation_status);
    } else {
        console.warn('validation_status field not found in doctype');
    }
    
    if (frm.fields_dict.overall_compliance_status) {
        frm.set_value('overall_compliance_status', overall_status);
    } else {
        console.warn('overall_compliance_status field not found in doctype');
    }
    
    // Save the document to persist the validation status
    frm.save().then(function() {
        console.log('Document validation status updated and saved to:', validation_status);
        
        // If validation passed, refresh the form to show the Submit button
        if (validation_status === 'Compliant') {
            console.log('Validation compliant - setting up submit button');
            
            // Multiple approaches to ensure button appears
            setTimeout(function() {
                console.log('Refreshing form for submit button...');
                frm.refresh();
            }, 500);
            
            setTimeout(function() {
                console.log('Setting up form buttons...');
                setup_form_buttons(frm);
            }, 1000);
            
            setTimeout(function() {
                console.log('Final button setup...');
                setup_form_buttons(frm);
            }, 1500);
        }
    }).catch(function(error) {
        console.error('Error saving document:', error);
        // Handle case where fields don't exist
        if (error.message && error.message.includes('validation_status')) {
            frappe.msgprint({
                title: __('Field Missing'),
                message: __('The validation_status field is missing from the COA AMB doctype. Please add it to enable full functionality.'),
                indicator: 'red'
            });
        }
    });
}

function show_compliance_results(results, frm) {
    var message = '<div style="margin: 10px 0;">' +
        '<h4>📊 TDS Compliance Results</h4>' +
        '<p><strong>Total Rows:</strong> ' + results.total + '</p>' +
        '<p><strong>✅ PASS:</strong> <span style="color: green;">' + results.passed + '</span></p>' +
        '<p><strong>❌ FAIL:</strong> <span style="color: red;">' + results.failed + '</span></p>' +
        '<p><strong>⏳ PENDING:</strong> <span style="color: orange;">' + results.pending + '</span></p>' +
        '<p><strong>📋 TITLE ROWS:</strong> <span style="color: blue;">' + results.title_rows + '</span></p>' +
        '<p><strong>🚫 SKIPPED:</strong> <span style="color: gray;">' + results.skipped + '</span></p>' +
        '</div>';
    
    if (results.issues.length > 0) {
        message += '<div style="margin: 10px 0;"><h5>🔍 Validation Issues:</h5><ul style="max-height: 200px; overflow-y: auto;">';
        results.issues.forEach(function(issue) {
            message += '<li>' + issue + '</li>';
        });
        message += '</ul></div>';
    } else {
        // Show success message when there are no issues
        message += '<div style="margin: 10px 0; padding: 10px; background-color: #d4edda; border-radius: 5px; border: 1px solid #c3e6cb;">' +
                   '<h5 style="color: #155724; margin: 0;">✅ All parameters passed validation successfully!</h5>' +
                   '</div>';
    }
    
    // Calculate overall status
    var validatedCount = results.passed + results.failed + results.pending;
    var overallStatus = 'PASS';
    var indicator = 'green';
    
    if (results.failed > 0) {
        overallStatus = 'FAIL';
        indicator = 'red';
    } else if (results.pending > 0 && validatedCount > 0) {
        overallStatus = 'PENDING';
        indicator = 'orange';
    }
    
    message += '<div style="background-color: ' + getStatusColor(indicator) + '; ' +
                'padding: 10px; border-radius: 5px; margin: 10px 0;">' +
                '<h4>Overall Status: <span style="color: ' + indicator + ';">' + overallStatus + '</span></h4>' +
                '</div>';
    
    // Add submit instructions if all validations pass
    if (results.failed === 0 && results.passed > 0) {
        message += '<div style="margin: 10px 0; padding: 10px; background-color: #d1ecf1; border-radius: 5px; border: 1px solid #bee5eb;">' +
                   '<h5 style="color: #0c5460; margin: 0;">📝 Next Steps:</h5>' +
                   '<p style="margin: 5px 0 0 0; color: #0c5460;">All parameters are compliant. The "Submit COA" button is now available for submission.</p>' +
                   '</div>';
        
        // Force multiple refreshes to ensure button appears
        setTimeout(function() {
            console.log('First refresh after compliance results...');
            frm.refresh();
        }, 500);
        
        setTimeout(function() {
            console.log('Second refresh and button setup...');
            setup_form_buttons(frm);
        }, 1000);
        
        setTimeout(function() {
            console.log('Final button setup...');
            setup_form_buttons(frm);
        }, 1500);
    }
    
    frappe.msgprint({
        title: __('TDS Compliance Validation'),
        message: message,
        indicator: indicator
    });
}

function submit_coa_document(frm) {
    frappe.confirm(
        __('Are you sure you want to submit this COA? This will start the approval workflow.'),
        function() {
