function setup_widget_events(widget) {
    // Refresh button
    widget.find('.refresh-announcements').click(function() {
        $(this).html('⏳');
        update_batch_announcements();
        setTimeout(() => $(this).html('🔄'), 1000);
    });
    
    // Minimize button
    widget.find('.minimize-announcement').click(function() {
        minimizeWidget(widget);
    });
    
    // Close button
    widget.find('.close-announcement').click(function() {
        widget.fadeOut(300, function() {
            $(this).remove();
        });
        
        // Re-show after 5 minutes
        setTimeout(function() {
            update_batch_announcements();
        }, 300000);
    });
    
    // Hover effects
    widget.hover(
        function() {
            $(this).css({
                'opacity': '1',
                'transform': 'scale(1)',
                'box-shadow': '0 8px 25px rgba(0,0,0,0.3)'
            });
        },
        function() {
            $(this).css({
                'opacity': '0.95',
                'transform': 'scale(1)',
                'box-shadow': '0 6px 20px rgba(0,0,0,0.25)'
            });
        }
    );
}

function minimizeWidget(widget) {
    widget.animate({
        width: '60px',
        height: '50px'
    }, 300);
    widget.find('.announcement-content, h4, small, .close-announcement, .refresh-announcements').hide();
    widget.find('.minimize-announcement').html('📋').attr('title', 'Expand');
    
    // Click to expand
    widget.find('.minimize-announcement').off('click').click(function() {
        widget.animate({
            width: BATCH_WIDGET_CONFIG.widgetWidth + 'px',
            height: 'auto'
        }, 300);
        widget.find('.announcement-content, h4, small, .close-announcement, .refresh-announcements').show();
        widget.find('.minimize-announcement').html('📌').attr('title', 'Minimize');
        setup_widget_events(widget);
    });
}

