function initializeBatchWidget() {
    // Wait for Frappe to be fully loaded and user to be logged in
    if (typeof frappe === 'undefined' || !frappe.session || frappe.session.user === 'Guest') {
        setTimeout(initializeBatchWidget, 1000);
        return;
    }

    console.log('🚀 Initializing Batch Navbar Widget for user:', frappe.session.user);
    addWidgetStyles();
    
    // Set up auto-refresh every 30 seconds
    setInterval(update_batch_announcements, 30000);
    
    // Initial load after 2 seconds
    setTimeout(update_batch_announcements, 2000);
}

function update_batch_announcements() {
    console.log('🔄 Fetching Batch AMB data...');
    
    frappe.call({
        method: 'frappe.client.get_list',
        args: {
            doctype: 'Batch AMB',
            fields: ['name', 'title', 'wo_start_date', 'status', 'custom_batch_level'],
            filters: { 'status': ['!=', 'Completed'] },
            limit: 10
        },
        callback: function(r) {
            if (r.message && r.message.length > 0) {
                console.log('✅ Found', r.message.length, 'active batches');
                process_batch_data(r.message);
            } else {
                console.log('ℹ️ No active batches found');
                show_no_batches_message();
            }
        },
        error: function(r) {
            console.log('⚠️ No batches found or connection issue');
            show_no_batches_message();
        }
    });
}

