// Function to recalculate entire row
function recalculate_row(frm, cdt, cdn) {
    var row = frappe.get_doc(cdt, cdn);
    
    // Calculate freight cost based on quantity
    var freight_cost = calculate_freight_cost(row.qty);
    frappe.model.set_value(cdt, cdn, 'freight_cost', freight_cost);
    
    // Apply quantity-based escalation
    var escalated_price = apply_quantity_escalation(row.price_list_rate, row.qty);
    frappe.model.set_value(cdt, cdn, 'escalated_rate', escalated_price);
    
    // Calculate final rate
    var final_rate = flt(escalated_price) + flt(freight_cost);
    frappe.model.set_value(cdt, cdn, 'rate', final_rate);
    
    // Calculate amount
    if (row.qty && final_rate) {
        var amount = flt(row.qty) * final_rate;
        frappe.model.set_value(cdt, cdn, 'amount', amount);
        
        // Recalculate discount if percentage is set
        if (row.discount_percentage) {
            calculate_discount(frm, cdt, cdn);
        }
    }

    // Calculate delivered price
    calculate_delivered_price(frm, cdt, cdn);
}

// Calculate delivered price (ex_works_price + freight_cost)
function calculate_delivered_price(frm, cdt, cdn) {
    var row = frappe.get_doc(cdt, cdn);
    var ex_works_price = flt(row.ex_works_price) || 0;
    var freight_cost = flt(row.freight_cost) || 0;
    
    var delivered_price = ex_works_price + freight_cost;
    frappe.model.set_value(cdt, cdn, 'delivered_price', delivered_price);
}

// Fetch freight cost from freight location and calculate delivered price
function fetch_freight_cost_and_calculate(frm, cdt, cdn) {
    var row = frappe.get_doc(cdt, cdn);
    
    if (row.freight_location) {
        frappe.call({
            method: "frappe.client.get_value",
            args: {
                doctype: "Freight Location", // Replace with your actual doctype name
                filters: { name: row.freight_location },
                fieldname: ["freight_cost"] // Replace with actual field name for freight cost
            },
            callback: function(r) {
                if (r.message && r.message.freight_cost) {
                    // Set the freight cost from the freight location
                    frappe.model.set_value(cdt, cdn, 'freight_cost', r.message.freight_cost);
                    
                    // Recalculate delivered price
                    calculate_delivered_price(frm, cdt, cdn);
                    
                    // Also trigger row recalculation
                    recalculate_row(frm, cdt, cdn);
                }
            }
        });
    }
}

// Calculate freight cost based on quantity
function calculate_freight_cost(quantity) {
    quantity = flt(quantity);
    if (quantity <= 1) return 58.30;
    if (quantity <= 5) return 19.87;
    if (quantity <= 10) return 16.06;
    if (quantity <= 20) return 14.17;
    return 13.91;
}

// Apply quantity-based price escalation
function apply_quantity_escalation(base_price, quantity) {
    base_price = flt(base_price);
    quantity = flt(quantity);
    
    if (quantity >= 100) return base_price * 0.9;      // 10% discount
    if (quantity >= 50) return base_price * 0.95;      // 5% discount
    return base_price;
}

// Calculate discount from percentage
function calculate_discount(frm, cdt, cdn) {
    var row = frappe.get_doc(cdt, cdn);
    if (row.discount_percentage && row.amount) {
        var discount_amount = flt(row.amount) * flt(row.discount_percentage) / 100;
        frappe.model.set_value(cdt, cdn, 'discount_amount', discount_amount);
        
        var net_amount = flt(row.amount) - discount_amount;
        frappe.model.set_value(cdt, cdn, 'net_amount', net_amount);
        
        if (row.qty) {
            var net_rate = net_amount / flt(row.qty);
            frappe.model.set_value(cdt, cdn, 'net_rate', net_rate);
        }
    }
}

// Calculate discount percentage from amount
function calculate_discount_from_amount(frm, cdt, cdn) {
    var row = frappe.get_doc(cdt, cdn);
    if (row.discount_amount && row.amount) {
        var discount_percentage = (flt(row.discount_amount) / flt(row.amount)) * 100;
        frappe.model.set_value(cdt, cdn, 'discount_percentage', discount_percentage);
        
        var net_amount = flt(row.amount) - flt(row.discount_amount);
        frappe.model.set_value(cdt, cdn, 'net_amount', net_amount);
        
        if (row.qty) {
            var net_rate = net_amount / flt(row.qty);
            frappe.model.set_value(cdt, cdn, 'net_rate', net_rate);
        }
    }
}

// Update stock details
function update_stock_details(frm, cdt, cdn) {
    var row = frappe.get_doc(cdt, cdn);
