function set_quantity_limits(frm, cdt, cdn) {
    var row = frappe.get_doc(cdt, cdn);
    
    switch(row.quantity_range) {
        case '1-5 kg':
            row.min_quantity = 1;
            row.max_quantity = 5;
            break;
        case '6-10 kg':
            row.min_quantity = 6;
            row.max_quantity = 10;
            break;
        case '11-25 kg':
            row.min_quantity = 11;
            row.max_quantity = 25;
            break;
        case '26-200 kg':
            row.min_quantity = 26;
            row.max_quantity = 200;
            break;
        default:
            row.min_quantity = 0;
            row.max_quantity = 0;
    }
    
    frappe.model.set_value(cdt, cdn, 'min_quantity', row.min_quantity);
    frappe.model.set_value(cdt, cdn, 'max_quantity', row.max_quantity);
}

function calculate_freight_based_on_type(frm, cdt, cdn) {
    var row = frappe.get_doc(cdt, cdn);
    var freight_cost = 0;
    
    // Set freight cost based on quantity range and freight type
    if (row.quantity_range && row.freight_type) {
        switch(row.quantity_range) {
            case '1-5 kg':
                freight_cost = (row.freight_type === 'Air Freight') ? 58.30 : 
                              (row.freight_type === 'Sea Freight') ? 45.00 :
                              (row.freight_type === 'Ground') ? 35.00 : 60.00;
                break;
            case '6-10 kg':
                freight_cost = (row.freight_type === 'Air Freight') ? 19.87 : 
                              (row.freight_type === 'Sea Freight') ? 15.00 :
                              (row.freight_type === 'Ground') ? 12.00 : 22.00;
                break;
            case '11-25 kg':
                freight_cost = (row.freight_type === 'Air Freight') ? 16.06 : 
                              (row.freight_type === 'Sea Freight') ? 12.00 :
                              (row.freight_type === 'Ground') ? 9.00 : 18.00;
                break;
            case '26-200 kg':
                freight_cost = (row.freight_type === 'Air Freight') ? 13.91 : 
                              (row.freight_type === 'Sea Freight') ? 10.00 :
                              (row.freight_type === 'Ground') ? 7.00 : 15.00;
                break;
        }
        
        frappe.model.set_value(cdt, cdn, 'freight_cost', freight_cost);
        calculate_prices(frm, cdt, cdn);
    }
}

function calculate_prices(frm, cdt, cdn) {
    var row = frappe.get_doc(cdt, cdn);
    
    if (row.ex_works_price && row.freight_cost && row.min_quantity && row.max_quantity) {
        // Calculate delivered price per kg
        var delivered_price = flt(row.ex_works_price) + flt(row.freight_cost);
        frappe.model.set_value(cdt, cdn, 'delivered_price', delivered_price);
        
        // Calculate price per kg
        frappe.model.set_value(cdt, cdn, 'price_per_kg', delivered_price);
        
        // Calculate total price range (min and max)
        var min_total = delivered_price * row.min_quantity;
        var max_total = delivered_price * row.max_quantity;
        
        // Display as range or single value
        if (row.min_quantity === row.max_quantity) {
            frappe.model.set_value(cdt, cdn, 'total_price', min_total);
        } else {
            frappe.model.set_value(cdt, cdn, 'total_price', max_total);
            // You could also display as range: min_total + " - " + max_total
        }
    }
}

