// Replace the add_item_row function with this updated version:

// Add actual row to items table
function add_item_row(frm, item_doc, quantity = 1) {
    console.log("=== ADDING ITEM TO TABLE ===");
    console.log("Item:", item_doc);
    console.log("Quantity:", quantity);
    
    // Check if item already exists in table
    var existing_row = frm.doc.items.find(function(item) {
        return item.item_code === item_doc.name;
    });
    
    if (existing_row) {
        frappe.msgprint(__('Item {0} already exists in the table at row {1}', 
            [item_doc.item_name, existing_row.idx]));
        return;
    }
    
    // Check if there's an empty row (row with no item_code) that we can reuse
    var empty_row = frm.doc.items.find(function(item) {
        return !item.item_code || item.item_code === '';
    });
    
    if (empty_row) {
        console.log("Found empty row, reusing it:", empty_row.idx);
        // Reuse the empty row instead of adding a new one
        empty_row.item_code = item_doc.name;
        empty_row.item_name = item_doc.item_name;
        empty_row.description = item_doc.description;
        empty_row.qty = quantity;
        empty_row.uom = item_doc.stock_uom;
        
        // Set custom fields from header
        if (frm.doc.custom_tds_amb) {
            empty_row.custom_tds_amb = frm.doc.custom_tds_amb;
        }
        if (frm.doc.custom_tds_item) {
            empty_row.custom_tds_item = frm.doc.custom_tds_item;
        }
        if (frm.doc.custom_coa_amb) {
            empty_row.custom_coa_amb = frm.doc.custom_coa_amb;
        }
        if (frm.doc.custom_coa_item) {
            empty_row.custom_coa_item = frm.doc.custom_coa_item;
        }
        
        // Refresh the items table
        frm.refresh_field("items");
        
        // Get item price and other details for the reused row
        get_item_price_and_details(frm, empty_row, item_doc);
        
        frappe.show_alert({
            message: __('Item {0} added to existing row with quantity {1}', [item_doc.item_name, quantity]),
            indicator: 'green'
        });
    } else {
        // No empty row found, add new row
        var child = frm.add_child("items");
        
        // Set basic item details
        child.item_code = item_doc.name;
        child.item_name = item_doc.item_name;
        child.description = item_doc.description;
        child.qty = quantity;
        child.uom = item_doc.stock_uom;
        
        // Set custom fields from header
        if (frm.doc.custom_tds_amb) {
            child.custom_tds_amb = frm.doc.custom_tds_amb;
        }
        if (frm.doc.custom_tds_item) {
            child.custom_tds_item = frm.doc.custom_tds_item;
        }
        if (frm.doc.custom_coa_amb) {
            child.custom_coa_amb = frm.doc.custom_coa_amb;
        }
        if (frm.doc.custom_coa_item) {
            child.custom_coa_item = frm.doc.custom_coa_item;
        }
        
        // Refresh the items table
        frm.refresh_field("items");
        
        // Get item price and other details
        get_item_price_and_details(frm, child, item_doc);
        
        frappe.show_alert({
            message: __('Item {0} added to new row with quantity {1}', [item_doc.item_name, quantity]),
            indicator: 'green'
        });
    }
}
// Get item price and additional details
function get_item_price_and_details(frm, child_row, item_doc) {
