// Update all TDS-related fields (existing function)
function update_tds_fields(frm, tds_doc, item_doc) {
    console.log("=== UPDATING FIELDS ===");
    
    var promises = [];
    
    // Update TDS AMB field
    promises.push(frm.set_value('custom_tds_amb', tds_doc.name));
    
    // Update TDS Item field - use the ITEM NAME (descriptive name)
    if (item_doc.item_name) {
        console.log("Setting TDS Item to Item Name:", item_doc.item_name);
        promises.push(frm.set_value('custom_tds_item', item_doc.item_name));
    }
    
    // Update COA fields if they exist in TDS
    if (tds_doc.custom_coa_amb) {
        promises.push(frm.set_value('custom_coa_amb', tds_doc.custom_coa_amb));
    }
    if (tds_doc.custom_coa_item) {
        promises.push(frm.set_value('custom_coa_item', tds_doc.custom_coa_item));
    }
    
    // Wait for all updates
    Promise.all(promises).then(() => {
        frm.refresh_fields();
        
        frappe.show_alert({
            message: __('TDS {0} selected and fields updated', [tds_doc.name]),
            indicator: 'green'
        });
    });
}

// Function to fetch TDS details from TDS AMB (existing function)
function fetch_tds_details(frm) {
    if (!frm.doc.custom_tds_amb) return;
    
    console.log("=== FETCHING TDS DETAILS ===");
    
    frappe.call({
        method: "frappe.client.get",
        args: {
            doctype: "TDS Product Specification",
            name: frm.doc.custom_tds_amb
        },
        callback: function(r) {
            if (r.message) {
                var tds = r.message;
                
                var item_code = tds.product_item || tds.item_code;
                if (item_code) {
                    frappe.call({
                        method: "frappe.client.get",
                        args: {
                            doctype: "Item",
                            name: item_code
                        },
                        callback: function(item_r) {
                            if (item_r.message) {
                                update_tds_fields(frm, tds, item_r.message);
                            } else {
                                update_tds_fields_without_item(frm, tds);
                            }
                        }
                    });
                } else {
                    update_tds_fields_without_item(frm, tds);
                }
            }
        }
    });
}

// Update fields when no item is linked in TDS (existing function)
function update_tds_fields_without_item(frm, tds_doc) {
    var promises = [];
    
    promises.push(frm.set_value('custom_tds_amb', tds_doc.name));
    
    if (tds_doc.item_name) {
        promises.push(frm.set_value('custom_tds_item', tds_doc.item_name));
    }
    
    if (tds_doc.custom_coa_amb) {
        promises.push(frm.set_value('custom_coa_amb', tds_doc.custom_coa_amb));
    }
    if (tds_doc.custom_coa_item) {
        promises.push(frm.set_value('custom_coa_item', tds_doc.custom_coa_item));
    }
    
    Promise.all(promises).then(() => {
        frm.refresh_fields();
        frappe.show_alert({
            message: __('TDS details updated'),
            indicator: 'green'
        });
    });
}

frappe.ui.form.on('Item', {
    item_name: function(frm) {
        // When item name changes, update linked TDS Product Specification
        if (frm.doc.custom_product_key_tds && frm.doc.item_name) {
            update_linked_tds(frm);
        }
    },
    
    after_save: function(frm) {
        // Also update after save to ensure sync
        if (frm.doc.custom_product_key_tds && frm.doc.item_name) {
            update_linked_tds(frm);
        }
    }
});

function update_linked_tds(frm) {
    frappe.call({
        method: "frappe.client.set_value",
        args: {
            doctype: "TDS Product Specification",
            name: frm.doc.custom_product_key_tds,
            fieldname: {
                item_name: frm.doc.item_name,
                item_code: frm.doc.name
            }
        },
        callback: function(r) {
            frappe.show_alert({
                message: __('Linked TDS Product Specification updated'),
                indicator: 'green'
            });
        },
        error: function(err) {
            frappe.msgprint(__('Error updating TDS: {0}', [err.message]));
        }
    });
}

frappe.ui.form.on('Batch AMB', {
    refresh: function(frm) {
        // Add real-time SPC dashboard
        if(!frm.spc_dashboard) {
            frm.spc_dashboard = new SPCDashboard(frm);
        }

        // Update quality indicators
        update_quality_indicators(frm);
    },

    // Auto-calculations
    total_gross_weight: function(frm) {
        calculate_net_weight(frm);
    },
    total_tara_weight: function(frm) {
        calculate_net_weight(frm);
    }
});

class SPCDashboard {
    constructor(frm) {
        this.frm = frm;
        this.init_dashboard();
    }

    init_dashboard() {
        // Add SPC compliance indicators
        this.frm.dashboard.add_indicator(__('SPC Compliance: 100%'), 'green');
        this.frm.dashboard.add_indicator(__('Quality Score: 98.7%'), 'blue');
        this.frm.dashboard.add_indicator(__('Parameters Monitored: 12'), 'orange');
    }
}

function calculate_net_weight(frm) {
    let gross = frm.doc.total_gross_weight || 0;
    let tara = frm.doc.total_tara_weight || 0;
    frm.set_value('total_net_weight', gross - tara);
}

function update_quality_indicators(frm) {
    // Update quality status based on test results
    if(frm.doc.quality_test_results && frm.doc.quality_test_results.length > 0) {
        let passed = frm.doc.quality_test_results.filter(t => t.status === 'Pass').length;
        let total = frm.doc.quality_test_results.length;
        let score = total > 0 ? (passed / total * 100).toFixed(1) : 0;

        frm.dashboard.data[1].value = __('Quality Score: {0}%', [score]);
    }
}
